mapper.select:
select find0.storeid as 'id',find0.storename as 'storeName',ifnull(find0.ordermytarget,0) as 'gongxian', ifnull(find1.xinxi,0) as 'luruxinxi', ifnull(find2.wanshan,0) as 'wanshanxinxi',ifnull(find3.weihu,0) as 'weihuxixni',ifnull(find4.genjin,0) as 'genjinkehu' from ( select * from ( SELECT * FROM( SELECT sum(su.mytarget*ss.score) AS ordermytarget, sst.`name` as 'storename', sst.id as storeid FROM (SELECT * from st_usertarget
assettype=1 and createtime BETWEEN (select date_format( #{startTime},'%Y-%m-%d 00:00:00')) and (select date_format( #{endTime},'%Y-%m-%d 23:59:59'))
) su LEFT JOIN st_user_task ss ON su.storestid = ss.stid and ss.type='2' LEFT JOIN system_store sst on su.sotreid = sst.id
sst.id = #{storeid}
) t
) t2 ) as find0 LEFT JOIN ( select count(*) as 'xinxi',storeid from client_user
storeid = #{storeid} and createtime BETWEEN (select date_format( #{startTime},'%Y-%m-%d 00:00:00')) and (select date_format( #{endTime},'%Y-%m-%d 23:59:59'))
) as find1 ON find1.storeid = find0.storeid LEFT JOIN (select count(*) as 'wanshan',storeid from client_record
storeid = #{storeid} and createtime BETWEEN (select date_format( #{startTime},'%Y-%m-%d 00:00:00')) and (select date_format( #{endTime},'%Y-%m-%d 23:59:59'))
) find2 ON find1.storeid = find2.storeid LEFT JOIN (select count(*) as 'weihu',storeid from system_usernotes
storeid = #{storeid} and createtime BETWEEN (select date_format( #{startTime},'%Y-%m-%d 00:00:00')) and (select date_format( #{endTime},'%Y-%m-%d 23:59:59')) and plantype = '0' and usertype> '0'
) find3 ON find1.storeid = find3.storeid LEFT JOIN (select count(*) as 'genjin',storeid from system_usernotes
storeid = #{storeid} and createtime BETWEEN (select date_format( #{startTime},'%Y-%m-%d 00:00:00')) and (select date_format( #{endTime},'%Y-%m-%d 23:59:59')) and plantype = '1'
) find4 ON find1.storeid = find4.storeid

select a.id,a.name,ifnull(b.alltotal,0) as alltotal from st_standard a LEFT JOIN (
select sum(a.alltotal) as alltotal,a.createtime,a.name,a.storestid,a.unit,a.sotreid from (
SELECT sum(t.mytarget) as alltotal,t.createtime, t. name,t.storestid,t.unit,t.sotreid from ( SELECT sug.*, st.`name`, st.unit FROM (SELECT * from st_usertarget
assettype=1 and sotreid = #{storeid}
) sug
LEFT JOIN st_standard st ON sug.storestid = st.id LEFT JOIN system_store ss on sug.sotreid = ss.id
) t
t.storestid >'10' and t.createtime BETWEEN (select date_format( #{startTime},'%Y-%m-%d 00:00:00')) and (select date_format( #{endTime},'%Y-%m-%d 23:59:59'))
GROUP by t.name
) a GROUP BY a.name ) b on a.id = b.storestid where a.status ='1'

select user.id as 'id',user.username as 'storeName', ifnull(userinfo.gongxian,0) as 'gongxian', ifnull(userinfo.luruxinxi,0) as 'luruxinxi', ifnull(userinfo.wanshanxinxi,0)as 'wanshanxinxi', ifnull(userinfo.weihuxixni,0)as 'weihuxixni', ifnull(userinfo.genjinkehu,0) as 'genjinkehu' from sys_user_admin user left JOIN (
select find0.id as 'id',find0.username as 'storeName',ifnull(find0.ordermytarget,0) as 'gongxian', ifnull(find1.xinxi,0) as 'luruxinxi', ifnull(find2.wanshan,0) as 'wanshanxinxi',ifnull(find3.weihu,0) as 'weihuxixni',ifnull(find4.genjin,0) as 'genjinkehu' from( select t2.id as id, t2.username as username, t2.storeid as storeid, sum(t2.mytarget*t2.score) AS ordermytarget from (
SELECT sst.id,sst.username,sst.storeid,su.storestid,su.mytarget,ss.score FROM sys_user_admin sst LEFT JOIN (SELECT * from st_usertarget
assettype=1 and createtime BETWEEN (select date_format( #{startTime},'%Y-%m-%d 00:00:00')) and (select date_format( #{endTime},'%Y-%m-%d 23:59:59')) and userid =#{storeid}
) su
ON sst.storeid=su.sotreid
LEFT JOIN st_user_task ss ON su.storestid = ss.stid
ss.type='2' and sst.id =#{storeid}
) as t2 GROUP BY t2.id
) as find0 LEFT JOIN ( select count(*) as 'xinxi' from client_user
userid=#{storeid} and createtime BETWEEN (select date_format( #{startTime},'%Y-%m-%d 00:00:00')) and (select date_format( #{endTime},'%Y-%m-%d 23:59:59'))
) find1 on 1=1 LEFT JOIN (select count(*) as 'wanshan' from client_record
userid=#{storeid} and createtime BETWEEN (select date_format( #{startTime},'%Y-%m-%d 00:00:00')) and (select date_format( #{endTime},'%Y-%m-%d 23:59:59'))
) find2 on 1=1 LEFT JOIN (select count(*) as 'weihu' from system_usernotes
userid=#{storeid} and createtime BETWEEN (select date_format( #{startTime},'%Y-%m-%d 00:00:00')) and (select date_format( #{endTime},'%Y-%m-%d 23:59:59')) and plantype = '0' and usertype> '0'
) find3 on 1=1 LEFT JOIN (select count(*) as 'genjin' from system_usernotes
userid=#{storeid} and createtime BETWEEN (select date_format( #{startTime},'%Y-%m-%d 00:00:00')) and (select date_format( #{endTime},'%Y-%m-%d 23:59:59')) and plantype = '1'
) find4 on 1=1 ) userinfo on 1=1 where user.id= #{storeid}

select a.id,a.name,ifnull(b.alltotal,0) as alltotal from st_standard a LEFT JOIN (
select sum(a.alltotal) as alltotal,a.createtime,a.name,a.storestid,a.unit,a.userid from (
SELECT sum(t.mytarget) as alltotal,t.createtime, t. name,t.storestid,t.unit,t.userid from ( SELECT sug.*, st.`name`, st.unit FROM (SELECT * from st_usertarget
assettype=1 and userid = #{storeid}
) sug
LEFT JOIN st_standard st ON sug.storestid = st.id LEFT JOIN system_store ss on sug.sotreid = ss.id
) t
t.storestid >'10' and t.createtime BETWEEN (select date_format( #{startTime},'%Y-%m-%d 00:00:00')) and (select date_format( #{endTime},'%Y-%m-%d 23:59:59'))
GROUP by t.name
) a GROUP BY a.name ) b on a.id = b.storestid where a.status ='1'

mapper.update:
update st_usermonthtarget
userid = #{userid}, target = #{target}, mytarget = #{mytarget}, createtime = #{createtime}, markes = #{markes}, storestid = #{storestid},
where id = #{id}
